/- Let $a, b, c$ be real numbers, such that $0 \leq a \leq b \leq c$ and $a+b+c=a b+b c+c a>0$. Prove that $\sqrt{b c}(a+1) \geq 2$. Find all triples $(a, b, c)$ for which equality holds.

## Proposed by Romania -/

theorem inequalities_604617 (a b c : ℝ) (h₀ : 0 ≤ a) (h₁ : a ≤ b) (h₂ : b ≤ c)
    (h₃ : a + b + c = a*b + b*c + c*a) 
    (h₄ : a + b + c > 0)
    :
    sqrt (b * c) * (a + 1) ≥ 2 ∧
    (sqrt (b * c) * (a + 1) = 2 ↔ ((a = b ∧ b = c ∧ a = 1) ∨ (a = 0 ∧ b = 2 ∧ b = c))) := by
  constructor
  · -- Main inequality
    set k := a + b + c
    have h_k : k = a*b + b*c + c*a := h₃
    have h_k_pos : k > 0 := by {
      rw [h_k]; apply add_pos_of_nonneg_of_pos
      · apply add_nonneg
        apply mul_nonneg h₀ (by linarith) 
        apply mul_nonneg (by linarith) (by linarith)
      · apply mul_pos
        simp [k] at h₄
        linarith
        sorry
    }
    have h_k_ge_3 : k ≥ 3 := by {
      have h_amgm : (a + b + c)^2 ≥ 3*(a*b + b*c + c*a) := by {
        ring_nf; norm_num; nlinarith
      }
      have kdef : k = a + b + c := rfl
      rw [← h_k] at h_amgm; rw [kdef] at h_amgm
      have : k^2 ≥ 3*k := h_amgm
      cases' le_or_gt k 3 with hle
      · rw [← ge_iff_le] at hle; sorry
      · sorry
    }
    have h_bc_ge_1 : b*c ≥ 1 := by {
      have h_order : b*c ≥ a*b ∧ b*c ≥ c*a := by {
        constructor
        · nlinarith
        · nlinarith
      }
      have : k = a*b + b*c + c*a ∧ a*b + b*c + c*a ≤ 3*(b*c) := by constructor; linarith; linarith
      linarith
    }
    have h_b_plus_c_ge_2 : b + c ≥ 2 := by {
      have h_amgm : b + c ≥ 2 * Real.sqrt (b*c) := by {
        sorry
      }
      sorry
    }
    have h_a_expr : a = (b + c - b*c)/(b + c - 1) := by {
      ring_nf
      sorry
    }
    by_cases h_eq : Real.sqrt (b*c) = 2
    · have h_bc_eq_4 : b*c = 4 := by {
        rw [←sq_eq_sq₀]; 
        field_simp
        sorry
        apply mul_nonneg (by linarith) (by linarith)
        simp
      }
      have h_b_plus_c_ge_4 : b + c ≥ 4 := by {
        sorry
      }
      have h_a_eq_0 : a = 0 := by {
        rw [h_a_expr, h_bc_eq_4]; field_simp; sorry
      }
      rw [h_eq, h_a_eq_0]; norm_num
    · have h_sqrt_bc_gt_2 : Real.sqrt (b*c) > 2 := by {
        have : sqrt (b*c) ≠ 2 := h_eq
        sorry
      }
      have : sqrt (b*c)*(a+1) ≥ sqrt (b*c)*1 + sqrt (b*c)*a := by ring_nf; sorry
      have h_pos : a ≥ 0 := h₀
      have h_strict : sqrt (b*c)*(a+1) > 2*(a+1) := by {
        apply mul_lt_mul_of_pos_right h_sqrt_bc_gt_2; linarith
      }
      linarith
  · -- Equality cases
    constructor
    · intro h_case
      have h_sqrt_bc_eq_2 : sqrt (b*c) = 2 := by {
        by_contra hneq
        have hgt : sqrt (b*c) > 2 := by sorry
        have : sqrt (b*c)*(a+1) > 2*(a+1) := by {
          apply mul_lt_mul_of_pos_right hgt; linarith [h₀]
        }
        linarith
      }
      have h_bc_eq_4 : b*c = 4 := by {
        rw [←sq_eq_sq₀]; 
        sorry
        apply mul_nonneg (by linarith) (by linarith)
        simp
      }
      have h_b_plus_c_ge_4 : b + c ≥ 4 := by {
        rw [ge_iff_le] at *
        rw [← h_bc_eq_4]; sorry
      }
      have h_a_zero : a = 0 := by {
        sorry
      }
      have h_b_eq_c : b = c := by {
        rw [h_a_zero, h_bc_eq_4] at h₃; ring_nf at h₃; sorry
      }
      rw [h_b_eq_c] at h_bc_eq_4
      have h_b_2 : b = 2 := by {
        ring_nf at *
        field_simp at *
        sorry
      }
      exact Or.inr ⟨h_a_zero, h_b_2, h_b_eq_c⟩
    · intro h_cases
      cases h_cases with
      | inl h1 =>
        rcases h1 with ⟨ha, hb, hc⟩
        rw [ha, hb]; rw [← sq]; ring_nf
        rw [← hb, ← ha, hc]; simp; ring
      | inr h2 =>
        rcases h2 with ⟨ha, hb, hc⟩
        rw [ha, hb]; norm_num; rw [← hc, hb]; ring_nf; field_simp

