/- 22. (KOR 2) Let $p$ be the product of two consecutive integers greater than 2. Show that there are no integers $x_{1}, x_{2}, \ldots, x_{p}$ satisfying the equation  $$ \sum_{i=1}^{p} x_{i}^{2}-\frac{4}{4 p+1}\left(\sum_{i=1}^{p} x_{i}\right)^{2}=1 $$  Alternative formulation. Show that there are only two values of $p$ for which there are integers $x_{1}, x_{2}, \ldots, x_{p}$ satisfying the above inequality. -/
theorem algebra_24141 (p : ℕ) (hp : ∃ k > 2, p = k * (k + 1)) :
    ¬∃ x : Fin p → ℤ, (∑ i : Fin p, (x i)^2) -
      (4 / (4 * p + 1)) * (∑ i : Fin p, x i)^2 = 1 := by 
  by_contra exist
  cases' exist with x xprop
  have pgt : 2 < p := by {
    obtain ⟨k, hkgt, hkeq⟩ := hp
    rw [hkeq]; rw [gt_iff_lt] at hkgt 
    have lem : k < k * (k + 1) := by {
      rw [Nat.lt_mul_iff_one_lt_right]; norm_num; 
      positivity; positivity
    }
    apply lt_trans hkgt; exact lem
  }
  let X := ∑ i : Fin p, x i;
  have Xdef : X = ∑ i : Fin p, x i := rfl
  wlog h : 0 ≤ X
  specialize this p hp x xprop
  apply this
  · exact pgt
  · rfl
  · sorry
  have lem1 : X < ∑ i : Fin p, (x i)^2 := by {
    rw [Xdef]; 
    apply Finset.sum_lt_sum 
    intro i hi; exact Int.le_self_sq (x i);
    use (⟨(p - 1), by {
      apply Nat.sub_one_lt_of_le
      have : 0 < 2 := by norm_num
      apply lt_trans this pgt; linarith
    }⟩);
    constructor
    · simp
    · sorry
  }
  /- continue here -/
  have S_eq : (∑ i : Fin p, (x i)^2) = (4/(4*p+1))*X^2 + 1 := sorry
  have S_ge : ∑ i : Fin p, (x i)^2 ≥ X + 1 := by {
    have diff : ∑ i, (x i)^2 - X > 0 := by linarith [lem1];
    sorry
  }
  have X_ge : X ≥ p + 1 := by {
    sorry
  }
  have X_sq_bound : X^2 ≤ p*(4*p+1) := by {
    sorry
  }
  have X_le : X ≤ 2*p := by {
    -- From X² ≤ p*(4*p+1) we deduce X ≤ 2p.
    sorry
  }
  have ratio : 1 ≤ (X:ℚ)/p ∧ (X:ℚ)/p ≤ 2 := by {
    constructor; { norm_cast; sorry }
    sorry
  }
  sorry

