theorem calculus_16465 (A B C D E F H : EuclideanSpace ℝ (Fin 2))
  (h₀ : A ≠ B)
  (h₁ : A ≠ C)
  (h₂ : B ≠ C)
  (h₃ : D ∈ segment ℝ A B)
  (h₄ : E ∈ segment ℝ B C)
  (h₅ : F ∈ segment ℝ A C)
  (h₆ : H ∈ segment ℝ A D)
  (h₇ : H ∈ segment ℝ B E)
