import olympiad; size(100); defaultpen(linewidth(0.8));
pair A = (0,0), B = (1,0), C = (0.5,sqrt(3)/2);
pair D = (A + B)/2, E = (B + C)/2, F = (C + A)/2;
pair M = intersectionpoint(A--E,B--F);
draw(A--B--C--cycle);
draw(A--E^^B--F^^C--D);
