[/asy] -/
theorem algebra_13663 {A B C D E F M : EuclideanSpace ℝ (Fin 2)}
  (hABC : A ≠ B ∧ A ≠ C ∧ B ≠ C)
  (hD : D = midpoint ℝ A B)
  (hE : E = midpoint ℝ B C)
  (hF : F = midpoint ℝ C A)
  (hM : M ∈ interior (convexHull ℝ {A, E, B}))
  (hM' : M ∈ interior (convexHull ℝ {B, E, C}))
  (hM'' : M ∈ interior (convexHull ℝ {C, E, A})) :
  (MeasureTheory.volume (convexHull ℝ {A, E, B})).toReal =
  (MeasureTheory.volume (convexHull ℝ {B, E, C})).toReal ∧
  (MeasureTheory.volume (convexHull ℝ {C, E, A})).toReal =
  (MeasureTheory.volume (convexHull ℝ {A, E, B})).toReal := by

