\[
\begin{array}{rrrrrrrrrrr}
4 & 9 & 1 & 2 & 9 & 3 & 0 & 8 & 2 & 1 & 3 \\
10 & 1 & 11 & 10 & 1 & 11 & 10 & 1 & 11 & 10 & 1
\end{array}
